/* Mini USP Strip */
.mini-usp-strip {
  background: var(--color-background, #f8f8f8);
  padding: 1.5rem 0;
  border-bottom: 1px solid var(--color-border, #eee);
}
.mini-usp-strip__container {
  max-width: 1200px;
  margin: 0 auto;
  text-align: center;
}
.mini-usp-strip__list {
  display: flex;
  justify-content: center;
  gap: 2.5rem;
  list-style: none;
  padding: 0;
  margin: 0;
  font-size: 1.1rem;
  font-weight: 500;
  letter-spacing: 0.02em;
}
.mini-usp-strip__list li {
  color: var(--color-foreground, #222);
  position: relative;
}
.mini-usp-strip__list li:not(:last-child)::after {
  content: "•";
  margin: 0 1.25rem;
  color: var(--color-border, #ccc);
}

/* Social Proof */
.social-proof {
  background: var(--color-background, #fff);
  padding: 1.5rem 0 1rem 0;
  text-align: center;
}
.social-proof__stars {
  font-size: 2rem;
  color: #f5b942;
  letter-spacing: 0.1em;
  margin-bottom: 0.25rem;
}
.social-proof__reviews {
  color: var(--color-foreground-secondary, #666);
  font-size: 1.1rem;
}

/* Intro Story */
.intro-story {
  background: var(--color-background, #f6f6f6);
  padding: 3rem 0 2rem 0;
  text-align: center;
}
.intro-story__container {
  max-width: 700px;
  margin: 0 auto;
}
.intro-story__container h2 {
  font-size: 2.2rem;
  margin-bottom: 0.5rem;
  font-weight: 700;
}
.intro-story__container p {
  font-size: 1.2rem;
  color: var(--color-foreground-secondary, #555);
}

/* Benefits Grid */
.benefits-grid {
  background: var(--color-background, #fff);
  padding: 2.5rem 0;
}
.benefits-grid__container {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 2rem;
  max-width: 1100px;
  margin: 0 auto;
  text-align: center;
}
.benefits-grid__item {
  background: var(--color-background-secondary, #f9f9f9);
  border-radius: 12px;
  padding: 2rem 1.5rem;
  box-shadow: 0 2px 12px 0 rgb(0 0 0 / 0.04);
  font-size: 1.1rem;
  font-weight: 500;
}
.benefits-grid__item strong {
  display: block;
  font-size: 1.2rem;
  margin-bottom: 0.5rem;
  color: var(--color-accent, #111);
}

/* Image/Text Split */
.image-text-split {
  background: var(--color-background, #f6f6f6);
  padding: 3rem 0;
}
.image-text-split__container {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 2.5rem;
  max-width: 1200px;
  margin: 0 auto;
}
.image-text-split__image {
  flex: 1 1 350px;
  min-width: 250px;
  max-width: 500px;
}
.image-text-split__image img {
  border-radius: 16px;
  box-shadow: 0 4px 24px 0 rgb(0 0 0 / 0.08);
  width: 100%;
  height: auto;
}
.image-text-split__text {
  flex: 2 1 400px;
  min-width: 250px;
}
.image-text-split__text h3 {
  font-size: 2rem;
  margin-bottom: 0.5rem;
  font-weight: 700;
}
.image-text-split__text p {
  font-size: 1.15rem;
  color: var(--color-foreground-secondary, #555);
}

/* Expanded Features */
.expanded-features {
  background: var(--color-background, #fff);
  padding: 2.5rem 0;
  text-align: center;
}
.expanded-features__container {
  max-width: 800px;
  margin: 0 auto;
}
.expanded-features__container h3 {
  font-size: 1.7rem;
  margin-bottom: 1rem;
  font-weight: 700;
}
.expanded-features__container ul {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-wrap: wrap;
  gap: 1.5rem;
  justify-content: center;
}
.expanded-features__container li {
  background: var(--color-background-secondary, #f6f6f6);
  border-radius: 8px;
  padding: 1rem 2rem;
  font-size: 1.1rem;
  font-weight: 500;
  box-shadow: 0 2px 8px 0 rgb(0 0 0 / 0.03);
}

/* Comparison Table */
.comparison-table {
  background: var(--color-background, #f6f6f6);
  padding: 2.5rem 0;
}
.comparison-table__container {
  max-width: 700px;
  margin: 0 auto;
}
.comparison-table table {
  width: 100%;
  border-collapse: collapse;
  background: #fff;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 2px 12px 0 rgb(0 0 0 / 0.04);
}
.comparison-table th,
.comparison-table td {
  padding: 1rem 1.5rem;
  text-align: center;
  font-size: 1.1rem;
}
.comparison-table th {
  background: var(--color-accent, #222);
  color: #fff;
  font-weight: 700;
}
.comparison-table tr:nth-child(even) td {
  background: var(--color-background-secondary, #f9f9f9);
}

/* UGC / Influencer Proof */
.ugc-influencer {
  background: var(--color-background, #fff);
  padding: 2.5rem 0 2rem 0;
  text-align: center;
}
.ugc-influencer__container h3 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  font-weight: 700;
}
.ugc-influencer__logos {
  display: flex;
  gap: 2rem;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
}
.ugc-influencer__logos img {
  height: 40px;
  width: auto;
  opacity: 0.7;
  filter: grayscale(1);
  transition: opacity 0.2s;
}
.ugc-influencer__logos img:hover {
  opacity: 1;
  filter: none;
}

/* Testimonials */
.testimonials {
  background: var(--color-background, #f6f6f6);
  padding: 3rem 0 2rem 0;
  text-align: center;
}
.testimonials__container {
  max-width: 800px;
  margin: 0 auto;
}
.testimonials__container h3 {
  font-size: 1.7rem;
  margin-bottom: 1.5rem;
  font-weight: 700;
}
.testimonials__container blockquote {
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 2px 12px 0 rgb(0 0 0 / 0.04);
  margin: 0 0 1.5rem 0;
  padding: 1.5rem 2rem 1rem 2rem;
  font-size: 1.15rem;
  color: var(--color-foreground, #222);
  position: relative;
}
.testimonials__container cite {
  display: block;
  margin-top: 0.5rem;
  font-size: 1rem;
  color: var(--color-foreground-secondary, #888);
  font-style: normal;
}

/* FAQ */
.faq {
  background: var(--color-background, #fff);
  padding: 2.5rem 0;
}
.faq__container {
  max-width: 700px;
  margin: 0 auto;
}
.faq__container h3 {
  font-size: 1.5rem;
  margin-bottom: 1.5rem;
  font-weight: 700;
}
.faq__container details {
  background: var(--color-background-secondary, #f9f9f9);
  border-radius: 8px;
  margin-bottom: 1rem;
  padding: 1rem 1.5rem;
  font-size: 1.1rem;
  box-shadow: 0 2px 8px 0 rgb(0 0 0 / 0.03);
}
.faq__container summary {
  font-weight: 600;
  cursor: pointer;
}

/* Final CTA */
.final-cta {
  background: var(--color-accent, #111);
  color: #fff;
  padding: 3rem 0 2rem 0;
  text-align: center;
}
.final-cta__container h2 {
  font-size: 2.2rem;
  margin-bottom: 1rem;
  font-weight: 700;
}
.final-cta__container .button {
  font-size: 1.2rem;
  padding: 1rem 2.5rem;
  border-radius: 8px;
  background: var(--color-accent, #fff);
  color: var(--color-background, #111);
  font-weight: 700;
  border: none;
  box-shadow: 0 2px 8px 0 rgb(0 0 0 / 0.08);
  transition: background 0.2s, color 0.2s;
}
.final-cta__container .button:hover {
  background: var(--color-background, #fff);
  color: var(--color-accent, #111);
}

/* Sticky Mobile CTA */
.sticky-mobile-cta {
  display: none;
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100%;
  z-index: 1000;
  background: var(--color-accent, #111);
  box-shadow: 0 -2px 12px 0 rgb(0 0 0 / 0.08);
}
.sticky-mobile-cta .button {
  width: 100%;
  border-radius: 0;
  font-size: 1.2rem;
  padding: 1.1rem 0;
  background: var(--color-accent, #fff);
  color: var(--color-background, #111);
  font-weight: 700;
  border: none;
}
@media (max-width: 749px) {
  .sticky-mobile-cta {
    display: block;
  }
  .image-text-split__container {
    flex-direction: column;
    gap: 1.5rem;
  }
  .mini-usp-strip__list {
    flex-direction: column;
    gap: 1rem;
  }
  .ugc-influencer__logos {
    gap: 1rem;
  }
}
