{% comment %}
  Email Signup Section
  Add a visually engaging newsletter signup form for your homepage.
  Customize the heading, subheading, and success message in the section settings.
{% endcomment %}

<section class="email-signup-section section spacing-style color-{{ section.settings.color_scheme }}">
  <div class="email-signup__container">
    {% if section.settings.heading != blank %}
      <h2 class="email-signup__heading">{{ section.settings.heading }}</h2>
    {% endif %}
    {% if section.settings.subheading != blank %}
      <p class="email-signup__subheading">{{ section.settings.subheading }}</p>
    {% endif %}
    <form method="post" action="/contact#contact_form" accept-charset="UTF-8" class="email-signup__form" id="email-signup-{{ section.id }}">
      <input type="hidden" name="form_type" value="customer" />
      <input type="hidden" name="utf8" value="âœ“" />
      <input type="email" name="contact[email]" class="email-signup__input" placeholder="Your email address" required>
      <button type="submit" class="email-signup__button button">{{ section.settings.button_label | default: 'Subscribe' }}</button>
    </form>
    {% if section.settings.success_message != blank %}
      <div class="email-signup__success-message" style="display:none;">{{ section.settings.success_message }}</div>
    {% endif %}
  </div>
</section>

{% schema %}
{
  "name": "Email Signup",
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "Heading",
      "default": "Sign up for our newsletter"
    },
    {
      "type": "text",
      "id": "subheading",
      "label": "Subheading",
      "default": "Get the latest updates, offers, and news."
    },
    {
      "type": "text",
      "id": "button_label",
      "label": "Button label",
      "default": "Subscribe"
    },
    {
      "type": "text",
      "id": "success_message",
      "label": "Success message",
      "default": "Thank you for subscribing!"
    },
    {
      "type": "color_scheme",
      "id": "color_scheme",
      "label": "Color scheme",
      "default": "scheme-1"
    }
  ],
  "presets": [
    {
      "name": "Email Signup"
    }
  ]
}
{% endschema %}

{% stylesheet %}
.email-signup-section {
  padding: 3rem 0;
  text-align: center;
}
.email-signup__container {
  max-width: 500px;
  margin: 0 auto;
}
.email-signup__heading {
  font-size: 2rem;
  margin-bottom: 0.5rem;
}
.email-signup__subheading {
  color: var(--color-foreground-secondary);
  margin-bottom: 1.5rem;
}
.email-signup__form {
  display: flex;
  gap: 0.5rem;
  justify-content: center;
}
.email-signup__input {
  flex: 1;
  padding: 0.75rem 1rem;
  border: 1px solid var(--color-border, #ccc);
  border-radius: 4px;
  font-size: 1rem;
}
.email-signup__button {
  padding: 0.75rem 1.5rem;
  font-size: 1rem;
  border-radius: 4px;
}
.email-signup__success-message {
  margin-top: 1rem;
  color: var(--color-success, green);
  font-weight: 500;
}
{% endstylesheet %}
